'use strict';
var path = require('path');
var util = require('util');
var ScriptBase = require('../script-base.js');
var angularUtils = require('../util.js');


module.exports = Generator;

function Generator() {
  ScriptBase.apply(this, arguments);
}

util.inherits(Generator, ScriptBase);

Generator.prototype.createComponentFiles = function createComponentFiles() {

	var nameSnakeCase = this.name.toSnakeCase();

  this.appTemplate('component', 'scripts/components/' + nameSnakeCase + '/' + nameSnakeCase);
  this.testTemplate('spec/component', 'components/' + nameSnakeCase + '/' + nameSnakeCase);
  this.template('component.html', path.join(this.env.options.appPath, 	'views/components/' + nameSnakeCase + '/' + nameSnakeCase + '.html'));
  this.template('component.scss', path.join(this.env.options.appPath, 	'styles/components/' + nameSnakeCase + '/' + '_' + nameSnakeCase + '.scss'));
  this.addScssToStyle('components/' + nameSnakeCase + '/' + nameSnakeCase);
  this.addScriptToIndex('components/' + nameSnakeCase + '/' + nameSnakeCase);
};