'use strict';
var path = require('path');
var util = require('util');
var ScriptBase = require('../script-base.js');
var angularUtils = require('../util.js');


module.exports = Generator;

function Generator() {
  ScriptBase.apply(this, arguments);
}

util.inherits(Generator, ScriptBase);

Generator.prototype.createServiceFiles = function createServiceFiles() {	

	var nameSnakeCase = this.name.toSnakeCase();

  this.appTemplate('service/provider', 'scripts/services/' + nameSnakeCase);
  this.testTemplate('spec/service', 'services/' + nameSnakeCase);
  this.addScriptToIndex('services/' + nameSnakeCase);
};
