'use strict';

angular.module('<%= _.camelize(appname) %>App')
	.directive('<%= _.camelize(name) %>', function () {

		var _linkFn = function link(scope, element, attrs) {
			element.text('this is the <%= _.camelize(name) %> directive');
		}

		return {
			template: '<div></div>',
			restrict: 'E',
			link: _linkFn
		};
	});